mapper.select:
select 

from exam_roll_user_answer
where user_answer_id = #{userAnswerId,jdbcType=VARCHAR}
  and user_id = #{userId,jdbcType=VARCHAR}
  and course_id = #{courseId,jdbcType=VARCHAR}

mapper.delete:
delete from exam_roll_user_answer
where user_answer_id = #{userAnswerId,jdbcType=VARCHAR}
  and user_id = #{userId,jdbcType=VARCHAR}
  and course_id = #{courseId,jdbcType=VARCHAR}

mapper.insert:
insert into exam_roll_user_answer (user_answer_id, user_id, course_id, 
  roll_id, question_id, user_answer, 
  true_answer, type, is_yes
  )
values (#{userAnswerId,jdbcType=VARCHAR}, #{userId,jdbcType=VARCHAR}, #{courseId,jdbcType=VARCHAR}, 
  #{rollId,jdbcType=VARCHAR}, #{questionId,jdbcType=VARCHAR}, #{userAnswer,jdbcType=VARCHAR}, 
  #{trueAnswer,jdbcType=VARCHAR}, #{type,jdbcType=VARCHAR}, #{isYes,jdbcType=BIT}
  )

mapper.update:
update exam_roll_user_answer


    roll_id = #{rollId,jdbcType=VARCHAR},


    question_id = #{questionId,jdbcType=VARCHAR},


    user_answer = #{userAnswer,jdbcType=VARCHAR},


    true_answer = #{trueAnswer,jdbcType=VARCHAR},


    type = #{type,jdbcType=VARCHAR},


    is_yes = #{isYes,jdbcType=BIT},


where user_answer_id = #{userAnswerId,jdbcType=VARCHAR}
  and user_id = #{userId,jdbcType=VARCHAR}
  and course_id = #{courseId,jdbcType=VARCHAR}

update exam_roll_user_answer
set roll_id = #{rollId,jdbcType=VARCHAR},
  question_id = #{questionId,jdbcType=VARCHAR},
  user_answer = #{userAnswer,jdbcType=VARCHAR},
  true_answer = #{trueAnswer,jdbcType=VARCHAR},
  type = #{type,jdbcType=VARCHAR},
  is_yes = #{isYes,jdbcType=BIT}
where user_answer_id = #{userAnswerId,jdbcType=VARCHAR}
  and user_id = #{userId,jdbcType=VARCHAR}
  and course_id = #{courseId,jdbcType=VARCHAR}
